mapper.select:
SELECT * FROM friendship
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY create_time DESC

SELECT * FROM friendship
WHERE user_id = #{userId} AND friend_id = #{friendId}
LIMIT 1

SELECT COUNT(*) FROM friendship
WHERE user_id = #{userId}

    AND status = #{status}

SELECT COUNT(*) FROM friendship
WHERE friend_id = #{userId}
AND status = 0

mapper.update:
UPDATE friendship
SET status = #{status}, update_time = NOW()
WHERE user_id = #{userId} AND friend_id = #{friendId}
